var link=document.querySelector(".btn-write"),popup=document.querySelector(".modal-content"),close=popup.querySelector(".modal-content-close"),form=popup.querySelector(".form-communication"),user=popup.querySelector("[id=signature]"),mail=popup.querySelector("[id=e-mail]"),storage=localStorage.getItem("user")
link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-content-show"),storage?(user.value=storage,mail.focus()):user.focus(),user.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-content-show"),popup.classList.remove("modal-error")}),form.addEventListener("submit",function(e){user.value&&mail.value?localStorage.setItem("user",user.value):(e.preventDefault(),popup.classList.add("modal-error"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&popup.classList.contains("modal-content-show")&&(popup.classList.remove("modal-content-show"),popup.classList.remove("modal-error"))})